import React from 'react';

function Dashboard() {
  // Example data for API integrations.
  const apiList = [
    { id: 1, name: 'OpenAI API', status: 'Connected' },
    { id: 2, name: 'Google Dialogflow', status: 'Disconnected' },
    { id: 3, name: 'Custom AI Model', status: 'Connected' },
  ];

  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold mb-6">Dashboard</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {apiList.map((api) => (
          <div key={api.id} className="p-4 bg-white shadow rounded-lg flex justify-between items-center">
            <div>
              <h3 className="text-lg font-bold">{api.name}</h3>
              <p className={`mt-1 text-sm ${api.status === 'Connected' ? 'text-green-600' : 'text-red-600'}`}>
                {api.status}
              </p>
            </div>
            <button className="bg-blue-500 hover:bg-blue-600 text-white py-1 px-3 rounded">
              {api.status === 'Connected' ? 'Manage' : 'Connect'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
